mapper.delete:
delete from goods_comment_info_tb
where id = #{id}
  and commenter_id =#{commenterId}

mapper.select:
SELECT goods_id as goodsId, count( 1 ) as commentCount FROM `goods_comment_info_tb` WHERE goods_id IN
#{goodsId}
GROUP BY goods_id

SELECT seller_id as sellerId, count( 1 ) as commentCount FROM `goods_comment_info_tb` WHERE seller_id IN
#{sellerId}
GROUP BY seller_id
