mapper.select:
select

from goods_like_info_tb


    and goods_id = #{goods_id}


    and liker_id = #{likerId}

SELECT goods_id as goodsId, count( 1 ) as likeCount FROM `goods_like_info_tb` WHERE goods_id IN
#{goodsId}
GROUP BY goods_id

SELECT seller_id as sellerId, count( 1 ) as likeCount FROM `goods_like_info_tb` WHERE seller_id IN
#{sellerId}
GROUP BY seller_id
